import React, { useEffect, useState } from 'react';
import data from './categorypost.json';

function Post(props) {
  const [post, setPost] = useState({
    id: '',
    title: '',
  });

  useEffect(() => {
    const postId = props.match.params.postid;
    const posts = data.data.find((post) => post.id == postId);
    setPost(posts);
  });
  return (
    <div>
      <h1>{post.title}</h1>
    </div>
  );
}

export default Post;
