import React, { useState, useEffect } from 'react';
import BlogData from './data.json';
import { NavLink } from 'react-router-dom';

function Rel(props) {
  const [post, setPost] = useState({});

  useEffect(() => {
    const postId = props.match.params.num;
    const number = BlogData.data.find((x) => x.id !== postId);
    setPost(number);
  });
  return (
    <div>
      {post.map((post) => {
        return (
          <NavLink to={`/post/${post.id}`}>
            <div>
              <h1>{post.blogTitle}</h1>
              <p>{post.postedOn}</p>
            </div>
          </NavLink>
        );
      })}
    </div>
  );
}

export default Rel;
