import React, { useState, useEffect } from 'react';
import CtPostDatata from '../../../Data/Category Post/categorypost.json';
import { NavLink } from 'react-router-dom';

function CtPost(props) {
  const [ctPost, setctPost] = useState();
  useEffect(() => {
    const category = props.match.params.category;
    const ctposts = CtPostDatata.data.find((ct) => ct.category == category);
    setctPost(ctposts);
  });
  console.log(ctPost);
  // if (ct.image == '') return null;
  return (
    <div>
      <h1>{ctPost.title}</h1>
    </div>
  );
}

export default CtPost;
