import React from 'react';
import CheckIcon from '@material-ui/icons/Check';
import DeleteIcon from '@material-ui/icons/Delete';
import { useState } from 'react';

const List = (props) => {
  // const [line, setLine] = useState(false);
  // const CheckFun = () => {
  //   console.log('click');
  //   setLine(true);
  // };

  const CheckFun = () => {
    var li = document.getElementById('check_btn');
    li.classList.add('check');
  };
  return (
    <ol>
      <li
        id='check_btn'
        // style={{
        //   textDecoration: line ? 'line-through' : 'none',
        //   opacity: line ? '0.1' : '1',
        // }}
      >
        {props.text}
      </li>
      <span className='list_btn'>
        <CheckIcon className='ch_btn btn' onClick={CheckFun} />

        <DeleteIcon className='del_btn btn' />
      </span>
    </ol>
  );
};

export default List;
