  //   const number = BlogData.data.find((x) => x.id == postId);
  //   setPost(number);
  //   console.log(postId);
  // }, post);

  return (
    <div>
      <h1>This is {post.blogTitle}</h1>
      {/* <BlogPost {...props} /> */}
    </div>
  );
};

export default Post;
