import React, { useState } from 'react';

const App = () => {
  const [num, setNum] = useState(5);

  const inc = () => {
    setNum(num + 1);
  };

  const dec = () => {
    if (num > 0) {
      setNum(num - 1);
    } else {
      setNum(0);
      alert('You Reached 0');
    }
  };

  return (
    <div className='main_div'>
      <div className='center_div'>
        <h1>{num}</h1>
        <div className='btn_div'>
          <button onClick={inc}>Inc</button>
          <button onClick={dec}>Dec</button>
        </div>
      </div>
    </div>
  );
};

export default App;
