import React, { useState, useEffect } from 'react';
import CtPostDatata from '../../../Data/Category Post/categorypost.json';
import { NavLink } from 'react-router-dom';

function CtPost(props) {
  const [ctPost, setctPost] = useState();
  useEffect(() => {
    const category = props.match.params.category;
    const ctposts = CtPostDatata.data.find((ct) => ct.category == category);
    setctPost(ctposts);
  });
  // if (ct.image == '') return null;
  return (
    <div>
      {ctPost.map((Ctdata) => {
        return (
          <NavLink to={`/category/${Ctdata.slug}`}>
            <img
              className='post__image'
              src={require(`../../Assets/Images/Project Image/${Ctdata.image}`)}
              alt='post image'
            />
            <h1 className='category__title'>{Ctdata.title}</h1>
          </NavLink>
        );
      })}
    </div>
  );
}

export default CtPost;
