import React, { useState, useEffect } from 'react';
import BlogData from './data.json';
import Rel from './Rel';
// import Category from './Category';

const Post = (props) => {
  const [post, setPost] = useState({});

  useEffect(() => {
    const postId = props.match.params.num;
    const number = BlogData.data.find((x) => x.id == postId);
    setPost(number);
    console.log(number);
  });

  // const [post, setPost] = useState({});
  // useEffect(() => {
  //   const postId = props.match.params.num;
  //   const number = BlogData.data.find((x) => x.id == postId);
  //   setPost(number);
  //   console.log(postId);
  // }, post);

  return (
    <div>
      <h1>This is {post.blogTitle}</h1>
      <p>{post.postedOn}</p>
      <h6>{post.blogText}</h6>
      <h4>Related Post</h4>
      <Rel />
      {/* <BlogPost {...props} /> */}
    </div>
  );
};

export default Post;
