import React, { useState } from 'react';

const App = () => {
  const [num, setNum] = useState(0);

  const myfun = () => {
    setNum(num + 1);
  };
  return (
    <div className='main_div'>
      <div className='center_div'>
        <h1>{num}</h1>
        <div className='btn_div'>
          <button onclick={myfun}>Inc</button>
          <button>Dec</button>
        </div>
      </div>
    </div>
  );
};

export default App;
