import React, { useState, useEffect } from 'react';
import CtPostDatata from '../../../Data/Category Post/categorypost.json';
import { NavLink } from 'react-router-dom';
import Card from '../../Ui Kits/Cart/CT Card/Card';

function CtPost(props) {
  const [ctPost, setctPost] = useState([]);
  useEffect(() => {
    const category = props.match.params.category;
    const ctposts = CtPostDatata.data.match((ct) => ct.category == category);
    setctPost(ctposts);
  }, [ctPost]);

  if (setctPost.title == '') return null;
  console.log(ctPost.title);
  return (
    <div>
      {ctPost.map((Post) => {
        return <NavLink to='/'>{/* <h1>{Post.title}</h1> */}</NavLink>;
      })}
    </div>
  );
}

export default CtPost;
