import React, { useEffect, useState } from 'react';
import data from './categorypost.json';

function Post(props) {
  const [post, setPost] = useState({
    id: '',
    title: '',
  });
  console.log(props);

  useEffect(() => {
    const postId = data.data;
    const posts = data.data.find((post) => post.id == postId);
    setPost(posts);
  });
  return (
    <div>
      {posts.map((post) => {
        return (
          <NavLink to={`${post.id}`}>
            <h1>{post.category}</h1>
          </NavLink>
        );
      })}
    </div>
  );
}

export default Post;
