import React from 'react';

function App(){
        let curTime = new Date().toLocaleTimeString();
        const[showCurTime, upDateCurTime] = useState(curTime);
    
        function upDateTime(){
            curTime = new Date().toLocaleTimeString();
            upDateCurTime(curTime);
        }

        return(
            <div>
                <h1>{showCurTime}</h1>
                <button onClick={upDateTime}>Click Me</button>
            </div>
        )
}

export default App;
