import React, { useState, useEffect } from 'react';
import CtPostDatata from '../../../Data/Category Post/categorypost.json';
import { NavLink } from 'react-router-dom';
import Card from '../../Ui Kits/Cart/CT Card/Card';

function CtPost(props) {
  const [ctPost, setctPost] = useState({
    id: '',
    category: '',
    title: '',
    slug: '',
    image: '',
  });
  useEffect((oldpost) => {
    const category = props.match.params.category;
    const ctposts = CtPostDatata.data.filter((ct) => ct.category == category);
    setctPost(ctposts);
    console.log(setctPost);
  });

  // if (ctPost.title == '') return null;
  return (
    <div>
      <h1>{ctPost.title}</h1>
      {/* {ctPost.map((Post) => {
        return (
          <NavLink to='/'>
            <h1>{Post.title}</h1>
          </NavLink>
        );
      })} */}
    </div>
  );
}

export default CtPost;
