import React, { useState } from 'react';

function App() {
  const [name, setName] = useState();

  const inputEvent = (event) => {
    setName(event.target.value);
  };

  //   const myfun = () => {};

  return (
    <div>
      <h1>
        Hello <span>{name}</span>
      </h1>

      <input
        type='text'
        id='input'
        placeholder='Type Your Name'
        onChange={inputEvent}
        value={name}
      />
      <button>Submit</button>
    </div>
  );
}

export default App;
