import React, { useState } from 'react';
import List from './List';

function App() {
  const [list, setList] = useState();
  const inputEvent = (event) => {
    console.log(list);
    setList(event.target.value);
  };

  return (
    <div className='maindiv'>
      <div className='centerdiv'>
        <div>
          <h1> ToDo List </h1>
        </div>
        <div className='INB'>
          <input
            type='text'
            id='input'
            placeholder='Type Your Name'
            onChange={inputEvent}
            value={list}
          />
          <button>+</button>
        </div>
        <List />
      </div>
    </div>
  );
}

export default App;
