import React from 'react';
import './post_form.css';
import { Avatar } from '@material-ui/core';
import VideoCallIcon from '@material-ui/icons/VideoCall';
import PhotoLibraryIcon from '@material-ui/icons/PhotoLibrary';
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions';

function Post_form() {
  return (
    <div className='main_div'>
      <div className='input__proPic'>
        <Avatar src='https://scontent.fdac31-1.fna.fbcdn.net/v/t1.0-1/cp0/p40x40/77001283_557475788129197_2099337519878373376_o.jpg?_nc_cat=100&ccb=2&_nc_sid=7206a8&_nc_eui2=AeH5LlRarhn40Q22k2n0K30BAAMcC2wWA54AAxwLbBYDntLJmRqb7wPbggrYgr2qxD8sZ1iARmVTVrXHMPozYKtO&_nc_ohc=u3sNR6HOtcIAX9Bigb4&_nc_ht=scontent.fdac31-1.fna&tp=27&oh=29b70e1f4a05bce2eb767079fb7e49ad&oe=5FB866EA' />
        <input type='text' placeholder="What's on your mind, Ritam Rahman" />
      </div>
      <div className='post__option'>
        <div>
          <VideoCallIcon style={{ color: 'red' }} />
          <h3>Live Video</h3>
        </div>
        <div>
          <PhotoLibraryIcon style={{ color: 'green' }} />
          <h3>Photo/video</h3>
        </div>
        <div>
          <EmojiEmotionsIcon style={{ color: 'orange' }} />
          <h3>Feeling/Activity</h3>
        </div>
      </div>
    </div>
  );
}

export default Post_form;
