import React from 'react';

//Date Show Function Start
var options = {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
};
const currnDate = new Date().toLocaleDateString('en', options);
//Date Show Function Start

function Topbar() {
  return (
    <div>
      <section id='topBar_section'>
        <div className='date'>
          <p>{currnDate}</p>
        </div>
      </section>
    </div>
  );
}

export default Topbar;
