import React, { useState, useEffect } from 'react';
import BlogData from './data.json';

const Post = (props) => {
  const [post, setPost] = useState({});
  useEffect(() => {
    const postId = props.match.params.postid;
    const number = BlogData.data.find((post) => post.id == postId);
    setPost(number);
  });

  return (
    <div>
      <h1>This is {post.blogTitle}</h1>
      {/* <BlogPost {...props} /> */}
    </div>
  );
};

export default Post;
