import React from 'react';
import Card from '../../Card/Card';
import './style.css';

//Date Show Function Start
var options = {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
};
const currnDate = new Date().toLocaleDateString('en', options);
//Date Show Function Start

function Topbar(props) {
  return (
    <div>
      <Card id='topBar__Card' width='80%' margin='auto'>
        <section className='topBar_section '>
          <div className='date'>
            <p>{currnDate}</p>
          </div>

          <div className='socialMedia__icon'>
            <img
              src={require('../../../Assets/Icon/fbicon.png')}
              alt='fbicon'
            />
            <img src={require('../../../Assets/Icon/tw.png')} alt='twicon' />
            <img src={require('../../../Assets/Icon/ins.png')} alt='insicon' />
          </div>
        </section>
      </Card>
    </div>
  );
}

export default Topbar;
