import React from 'react';
import CheckIcon from '@material-ui/icons/Check';
import DeleteIcon from '@material-ui/icons/Delete';
import { useState } from 'react';

const List = (props) => {
  const [line, setLine] = useState(false);
  const CheckFun = () => {
    setLine(true);
  };

  return (
    <ol>
      <li
        id='check_btn'
        style={{
          textDecoration: line ? 'line-through' : 'none',
          opacity: line ? '0.5' : '1',
          marginLeft: line ? '38px' : '0',
          transition: line ? '.25s all linear' : '0',
          color: line ? 'black' : '',
        }}
      >
        {props.text}
      </li>
      <span className='list_btn'>
        <CheckIcon
          className='ch_btn btn'
          onClick={CheckFun}
          style={{
            display: line ? 'none' : 'inline-block',
          }}
        />

        <DeleteIcon className='del_btn btn' onClick={props.onSelect} />
      </span>
    </ol>
  );
};

export default List;
