import React, { useState } from 'react';
import Layout from '../Layout/Layout';
import './project.css';
import CtCard from '../Ui Kits/Cart/CT Card/CtCard';
import { NavLink } from 'react-router-dom';
import Category from '../../Data/Category Data/category.json';
// images
import html from '../Assets/Images/Project Image/html.png';
import javascript from '../Assets/Images/Project Image/javascript.png';
import bootstrap from '../Assets/Images/Project Image/bootstrap.png';
import react from '../Assets/Images/Project Image/react.jpg';
import wordpress from '../Assets/Images/Project Image/wordpress.jpg';
import design from '../Assets/Images/Project Image/design.jpg';

function Project(props) {
  console.log(Category.data);
  const Ctdata = Category.data;

  return (
    <div>
      <Layout>
        <div className='pr_main_div'>
          <h1>Project Category</h1>
          <div className='category'>
            {/* --- html/css Start--- */}

            {Ctdata.map((Ctdata) => {
              return (
                <NavLink to={`/post/${Ctdata.category}`}>
                  <img
                    className='post__image'
                    src={require(`../Assets/Images/Project Image/${Ctdata.image}`)}
                    alt='post image'
                  />
                  <h1 className='category__title'>{Ctdata.title}</h1>
                </NavLink>
              );
            })}

            {/* --- html/css End--- */}
          </div>
        </div>
      </Layout>
    </div>
  );
}

export default Project;
