import React from 'react';
import './layout.css';
import Sidebar from '../Sidebar/Sidebar';

function Layout(props) {
  return (
    <div
      className='layout'
      style={{
        position: props.position ? props.width : 'fixed',
      }}
    >
      <Sidebar />

      <div className='props_child'>{props.children}</div>
    </div>
  );
}

export default Layout;
