import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import FavoriteBorderRoundedIcon from '@material-ui/icons/FavoriteBorderRounded';
import ModeCommentOutlinedIcon from '@material-ui/icons/ModeCommentOutlined';
import SendOutlinedIcon from '@material-ui/icons/SendOutlined';
import './post.css';

function Post() {
  return (
    <div className='post'>
      {/* post Body */}
      <div className='post__body'>
        {/* avatar,username */}
        <div className='post__header'>
          <Avatar
            alt='Remy Sharp'
            src='/static/images/avatar/1.jpg'
            className='avatar'
          />

          <h5 className='user__name'>cristofer_Lolan</h5>
        </div>
        {/* post iamge */}
        <img
          className='post__img'
          src='https://www.gstatic.com/tv/thumb/persons/233377/233377_v9_bb.jpg'
          alt='Cristofer Lolan'
        />
        {/* like/comment/shear */}
        <div className='like__div'>
          <FavoriteBorderRoundedIcon className='like' />
          <ModeCommentOutlinedIcon className='cmt' />
          <SendOutlinedIcon className='shear' />
        </div>
        {/* caption */}
        <div className='caption'>
          {/* cp_usernae */}
          <h5 className='cp__user_name'>cristofer_Lolan</h5>
          {/* main_caption */}
          <p>
            Lorem ipsum dolor sit amet consectetur, adipisicing elit. Deserunt,
            corporis aspernatur fugit quaerat fugiat cum optio soluta doloribus.
          </p>
        </div>
        {/* comment_area */}
        <div className='comment__area'>
          <input></input>
          <h4 className='cmt__post' onFocus>
            post
          </h4>
        </div>
      </div>
    </div>
  );
}

export default Post;
